import Foundation

class SelectionDemo {

    public func fib(n: Int) {
        let a = 1
        let b = 1

        let tmp = 0

        if (n < 2) {
            return 1
        }

        for (let i = 0; i < (n - 1); i = i + 1) {
            tmp = b;
            b = a;
            a = a + tmp;
        }

        return a;
    }
}
